mapper.insert:

insert into ts_role (ROLE_ID, ROLE_NAME, ROLE_DESCRIPTION, 
  ROLE_TYPE1, ROLE_TYPE2, CREATE_DATE, 
  UPDATE_DATE, IS_DELETE, ROLE_SHIRO
  )
values (#{roleId,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, #{roleDescription,jdbcType=VARCHAR}, 
  #{roleType1,jdbcType=VARCHAR}, #{roleType2,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, 
  #{updateDate,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=BIT}, #{roleShiro,jdbcType=VARCHAR}
  )

mapper.select:
	select
		tr.*
	from ts_role tr
	WHERE tr.IS_DELETE = false

    and tr.ROLE_ID = #{stringParam1,jdbcType=VARCHAR}

  limit #{pageNumber}, #{pageSize}
